import React from  'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import { makeStyles } from '@mui/styles';
import { Typography, Button, Grid, TextField } from '@mui/material'

import DisplayAppBar from './DisplayAppBar';

export default function LoginPage() {

    const [username, setUsername] = React.useState('');
    const [password, setPassword] = React.useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            axios.post('http://localhost:8000/login/', {
                username: username,
                password: password
            }).then((response) => {
                if (response.data.success) {
                    return <Redirect to="/home" />;
                } else {
                    console.log('Login failed');
                    
                }
            });
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <div>
            <DisplayAppBar />
            <Grid container direction="column" justifyContent="center" alignItems="center" spacing={2} >
                <Grid item>
                    <TextField required label="Username" variant="outlined" id="username" onChange={(e) => setUsername(e.target.value)}/>
                </Grid>
                <Grid item>
                    <TextField required label="Password" variant="outlined" id="password" onChange={(e) => setPassword(e.target.value)}/>
                </Grid>
                <Grid item>
                    <Button variant="outlined" onClick={handleLogin}>Login</Button>
                </Grid>
            </Grid>
        </div>
    )
};
